import * as yaml from "js-yaml";
export class Secret {
    content;
    constructor(content) {
        this.content = content;
    }
}
export const secret = (content) => new Secret(content);
export const secretYamlType = new yaml.Type("!secret", {
    kind: "scalar",
    construct: (data) => secret(data),
    represent: (data) => data.content,
    instanceOf: Secret,
});
